pageContext.rootQuery:
query PostBySlug($uid: String!) {
  prismic {
    allProjects(uid: $uid) {
      edges {
        node {
          project_content
          project_name
          title
          summary {
            summary_title
            summary_value
          }
          category
          _meta {
            uid
          }
        }
      }
    }
  }
}
